function quote(){$.ajax({url:"http://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=1",success:function(t){var e=t.shift(),n=e.content.slice(3);return n=n.substring(0,n.length-5),n=$.trim(n),$(".quote-place").html("<p><span>&ldquo;</span> "+n+"<span>&rdquo;</span><div class='author'><h4><em>- "+e.title+"</em></h4></div>").fadeIn(400),document.getElementById("tweet").setAttribute("href","https://twitter.com/intent/tweet?text="+n),!1},cache:!1})}$(document).ready(function(){quote()}),$("#get-quote").on("click",function(t){$(".quote-place").fadeOut(400),t.preventDefault(),quote()}),$(document).keydown(function(t){32!==t.which&&39!==t.which&&78!==t.which||($(".quote-place").fadeOut(400),t.preventDefault(),quote())});