function quote(){$.ajax({url:"http://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=1",success:function(e){var t=e.shift(),n=t.content.slice(3);n=n.substring(0,n.length-5),n=$.trim(n),$(".quote-place").html("<p><span>&ldquo;</span> "+n+"<span>&rdquo;</span><div class='author'><h4><em>- "+t.title+"</em></h4></div>").fadeIn(400);var c=encodeURI(n).replace(/&#8217;/g,"%27").replace(/"/g,"%22").replace(/&#8211;/g,"%2D").replace(/;/g,"%3B").replace(/#8220%3B;/g,"%3B");return document.getElementById("tweet").setAttribute("href","https://twitter.com/intent/tweet?text="+c),!1},cache:!1})}$(document).ready(function(){quote()}),$("#get-quote").on("click",function(e){$(".quote-place").fadeOut(400),e.preventDefault(),quote()}),$(document).keydown(function(e){32!==e.which&&39!==e.which&&78!==e.which||($(".quote-place").fadeOut(400),e.preventDefault(),quote())});