function quote(){$.ajax({url:"http://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=1",success:function(t){var e=t.shift(),n=e.content.slice(3);n=n.substring(0,n.length-5),n=$.trim(n),$(".quote-place").html("<p><span>&ldquo;</span> "+n+"<span>&rdquo;</span> <div class='author'><h4><em>- "+e.title+"</em></h4></div>")},cache:!1})}$(document).ready(function(){quote()}),$("#get-quote").on("click",function(t){t.preventDefault(),quote()});